<script>							
$(function(){
	var um = UM.getEditor('EmployContent');
	 um.addListener("contentChange",function(){
		if(UM.getEditor('EmployContent').hasContents()){
			$('#SubmitEmploy').attr('disabled',false);
		}
	 });
})
function Employ(uid,id){
	
	bootbox.hideAll();
	if(uid && id){
		var title='购买服务';
	}else{
		var title='雇用服务商';
		id=0
	}

	$('#EmployUid').val(uid);
	$('#Employid').val(id);
	
	$('#EmployModalTitle').html(title);
			
	$('#EmployModal').modal();

	$.getJSON('/get/employ/event/uid/'+uid+'/id/'+id, function(json){
		if(json.status=='ok'){
			
			var employform='';
			
			if(json.data.id){
				 $('#EmployMoney').val(json.data.money);
				 $('#EmploySubject').html(json.data.subject);
				 $('#EmployPicture').attr('src',SURL+'/'+json.data.picture);
				 $('#EmployDescribe').html(json.data.category_name_1+' '+json.data.category_name_2+' '+json.data.category_name_3);
				 $('#EmployFollow').html('关注');
				 $('#EmployFollowNum').html(json.data.show_number);
				 $('#EmployFavorites').html('收藏');
				 $('#EmployFavoritesNum').html(json.data.favorites_number);
				 $('#EmploySell').html('出售');
				 $('#EmploySellNum').html(json.data.sell_number);
			}
			
			if(json.data.moneyin>=0 && !json.data.id){
				$('#EmploySubject').html(json.data.username);
				if(json.data.provname && json.data.cityname && json.data.distname){
					$('#EmployDescribe').html(json.data.provname+' '+json.data.cityname+' '+json.data.distname);
				}else{
					$('#EmployDescribe').html('城市未填');
				}
				 $('#EmployPicture').attr('src',$(json.data.picture).attr('src'));
				 $('#EmployFollow').html('收入');
				 $('#EmployFollowNum').html(json.data.moneyin/100);
				 $('#EmployFavorites').html('投稿');
				 $('#EmployFavoritesNum').html(json.data.draft_number);
				 $('#EmploySell').html('中标');
				 $('#EmploySellNum').html(json.data.success_number);
			}
			

			return false; 

		}
		bootbox.hideAll();
	});		
	
}

function SubmitEmploy(){
	var money=$('#EmployMoney').val();
	var phone=$('#EmployPhone').val();
	
	if(isNaN(parseInt(money)) && !parseInt(money)){
		alert('请输入购买金额！');
		return false; 
	}
	
	if(isNaN(parseInt(phone)) && !parseInt(phone)){
		alert('请输入手机号码！');
		return false; 
	}
	
	
	 if(!UM.getEditor('EmployContent').hasContents()){
		 alert('请填写购买需求！');
		 return false; 
	 }
	 
	 var content=UM.getEditor('EmployContent').getContent();
	 
	var uid=$('#EmployUid').val();
	var id=$('#Employid').val();
				 
	bootbox.confirm({  
	   buttons: {  
		  confirm: {  
			 label: '确认',  
		  },  
		  cancel: {  
			 label: '取消',  
		  }  
	   },  
	   title:'提示',
	   message: '<i class="glyphicon glyphicon-warning-sign"></i> 您确认要支付'+money+'元?',  
		   callback: function(result) {  
			  if(result) {  
				 bootbox.hideAll();
				 
				 $.post('/post/employ/buy',{id:id,uid:uid,money:money,phone:phone,content:content}, function(json) {
							
						$('.bootbox').PhpappProgress({json:json});

						return true;
							
				 }, 'json');
				 
				  bootbox.dialog({
						 message: '<div class="progress progress-striped active"><div style="width: 1%" aria-valuemax="100" aria-valuemin="0" aria-valuenow="20" role="progressbar" class="progress-bar progress-bar-info"><span>0%</span></div></div>',
						 title: '<i class="icon fa fa-retweet"></i> 提交中',
						 closeButton: false
				 }).PhpappProgress({time:5000});
			 
				  return false; 
	  
			  }
		   } 

	})
   
	return false;
		
}

</script>

 

